import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import PropTypes from "prop-types";
import { Link, withRouter } from "react-router-dom";
import Register from '../../components/Register/Register';

import Input from '../../components/UI/Input/Input';
import Button from '../../components/UI/Button/Button';
import Spinner from '../../components/UI/Spinner/Spinner';
import classes from './Auth.module.css';
import * as actions from '../../store/actions/index';
import { updateObject, checkValidity } from '../../shared/utility';

const login = React.memo(props => {
    const [controls, setUser] = useState({
            username: '',
            password: '',
            errors: {}
    });

    const [authenticated, setAuthenticated] = useState(false);

    const { auth, errors, loginUser } = props;

    useEffect(() => {
      // console.log(nextProps);
      console.log(props);

      if (props.auth.isAuthenticated) {
        props.history.push("/register" + props.match.params.id);
      }
      if (props.errors) {
        setUser({
          errors: props.errors
        });
      }

    }, [])

    const inputChangedHandler = (event) => {
        setUser({ [event.target.id]: event.target.value});
    };

    const submitHandler = (event) => {
        event.preventDefault();
        setAuthenticated(true);
        const userData = { username: user.username, password: user.password};
        props.onAuth( controls.email.value, controls.password.value, isSignup );
        props.loginUser(userData);
    };

    return (
      <div className="container">
        <div style={{ marginTop: "4rem", paddingTop: "70px" }} className="row">
          <div className="col s8 offset-s2">
            <Link to="/" className="btn-flat waves-effect">
              <i className="material-icons left">keyboard_backspace</i> Back to
              home
            </Link>
            <div className="col s12" style={{ paddingLeft: "11.250px" }}>
              <h4>
                <b>Login</b> below
              </h4>
              <p className="grey-text text-darken-1">
                Ainda n√£o tem uma conta? <Link to="/auth">Registrar</Link>
              </p>
            </div>
            <form noValidate onSubmit={submitHandler}>
              <div className="input-field col s12">
                <input
                  onChange={inputChangedHandler}
                  value={user.email}
                  error={user.errors}
                  id="email"
                  type="email"

                />
                <label htmlFor="email">Email</label>

              </div>
              <div className="input-field col s12">
                <input
                  onChange={inputChangedHandler}
                  value={user.password}
                  error={user.errors}
                  id="password"
                  type="password"

                />
                <label htmlFor="password">Password</label>

              </div>
              <div className="col s12" style={{ paddingLeft: "11.250px" }}>
                <button
                  style={{
                    width: "150px",
                    borderRadius: "3px",
                    letterSpacing: "1.5px",
                    marginTop: "1rem"
                  }}
                  type="submit"
                  className="btn btn-large waves-effect waves-light hoverable blue accent-3"
                >
                  Login
                </button>
              </div>
            </form>
          </div>
        </div>
        {authenticated ? <Register /> : null}
      </div>
    );
});

login.propTypes = {
  loginUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  error: PropTypes.object.isRequired
};

// const mapStateToProps = state => {
//  return (
//   auth: state.auth,
//   errors: state.errors,
//   loginUser: state.loginUser
//   )
// };

const mapStateToProps = state => {
  return {
      loading: state.auth.loading,
      error: state.auth.error,
      isAuthenticated: state.auth.token !== null,
      registerRedirectPath: state.auth.registerRedirectPath,
      loginUser: state.auth.userId
  };
};

const mapDispatchToProps = dispatch => {
  return {
      onAuth: ( email, password, isSignup ) => dispatch( actions.auth( email, password, isSignup ) ),
      onSetRegisterRedirectPath: () => dispatch( actions.setRegisterRedirectPath( '/register/' ) )
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(login);
